import { css } from 'lit-element';
export default css `:host{--button-font-weight-semibold: 600;--button-primary-background-color: var(--primary);--button-primary-font-color: var(--neutral);--button-secondary-background-color: var(--secondary);--button-secondary-font-color: var(--neutral)}.button-container{min-width:80px;height:40px;border-radius:4px;font-family:var(--primary-font-family);font-size:1.6rem;color:var(--button-primary-font-color);font-weight:var(--button-font-weight-semibold);background-color:var(--button-primary-background-color);position:relative;overflow:hidden;border:none;width:100%}.button-container[button-size='large-btn']{height:48px}.button-container[button-size='small-btn']{min-width:48px;height:24px;font-size:1.2rem}.button-container[disabled-button=true]{height:48px;background-color:var(--disabled-text)}.button-container[button-type='secondary-btn']{background-color:var(--button-secondary-background-color);color:var(--button-secondary-font-color)}.button-container[button-type='tertiary-btn']{background-color:var(--button-secondary-font-color);color:var(--button-secondary-background-color);border:2px solid var(--button-secondary-background-color)}.button-container[button-type='floating-btn']{max-width:100%;height:48px;border-radius:2px;color:var(--secondary);background-color:var(--neutral);box-shadow:0 8px 12px 3px rgba(0,0,0,0.16)}.tertiary-disabled{min-width:48px;height:24px;border-radius:4px;border:2px solid var(--disabled-text);font-family:var(--primary-font-family);font-size:1.6rem;color:var(--disabled-text);background-color:var(--button-secondary-font-color);font-weight:var(--button-font-weight-semibold);position:relative;overflow:hidden;width:100%}.link-container{width:auto;font-family:var(--primary-font-family);font-size:1.4rem;color:var(--secondary);font-weight:var(--button-font-weight-semibold)}.link-container[button-size='small-btn']{font-size:1.2rem}.link-container:hover{height:36px}i{color:var(--secondary)}i[button-size='icon-btn-type1']{font-size:32px !important}i[button-size='icon-btn-type2']{font-size:24px !important}i[button-size='icon-btn-type3']{font-size:20px !important}i[button-size='icon-btn-type4']{font-size:16px !important}i[disabled-button=true]{color:var(--disabled-text)}`;
